  try {
    const filterData = _objectSpread({}, getRequestBody(name), {
      lang
    });

    const res = yield call(getMenuListRequest, filterData);
    const {
      data: {
        result: {
          rows
        }
      }
    } = yield call(getBestSellerRequest, filterData);
    yield put(getMenuListSuccess({
      data: getNavFilteredDataToView(_objectSpread({}, res.data, {
        messages
      })),
      name,
      bestseller: rows[0]
    }));
  } catch (error) {
    console.log(error);
  }
}

function* watchMenuList() {
  yield takeLatest(getMenuList, workMenuList);
}

export { // eslint-disable-next-line import/prefer-default-export
watchMenuList };
