import * as Yup from 'yup';
import { regex } from '../../../constants/utils'; // eslint-disable-next-line import/prefer-default-export

export const validationSchema = Yup.object().shape({
  firstName: Yup.string().required().trim().min(3),
  lastName: Yup.string().required().trim().min(3),
  email: Yup.string().email().required(),
  phone: Yup.string().required('required').test('phone', 'invalid phone number', value => regex.phone.test(value)).min(6),
  changePassMode: Yup.bool(),
  password: Yup.string().min(6, 'password min length should be 6 symbols').test('changePassMode', 'passwords are required', function (value) {
    if (this.parent.changePassword) {
      if (!value || !value.trim()) {
        return false;
      }
    }

    return true;
  }),
  confirmPassword: Yup.string().oneOf([Yup.ref('password'), null], 'Passwords must match').test('changePassMode', 'passwords are required', function (value) {
    if (this.parent.changePassword) {
      if (!value || !value.trim()) {
        return false;
      }
    }

    return true;
  })
});
