import React, { useState, useRef, useEffect } from 'react';
import PropTypes from 'prop-types';

import Input from 'antd/lib/input';
import Spin from 'antd/lib/spin';

import { useDispatch, useSelector } from 'react-redux';
import Link from 'next/link';
import { useRouter } from 'next/router';
import {
  selectSearchData,
  selectSearchDataLoading,
} from '../../store/search/reducer';
import useOnClickOutside from '../../hooks/useOnOutClick';
import { searchProductReset } from '../../store/search/action';

const { Search } = Input;

function SearchComponent({
  placeholder,
  className,
  onSearch,
  style,
  enterButton,
}) {
  const [searchValue, setValue] = useState();
  const dispatch = useDispatch();
  const resetSearch = () => dispatch(searchProductReset());
  const router = useRouter();

  useEffect(() => {
    setValue('');
  }, [router.pathname, router.asPath]);
  const data = useSelector(selectSearchData);
  const loading = useSelector(selectSearchDataLoading);
  const [focused, setFocused] = useState(false);
  const ref = useRef(null);

  const handleSearch = (value) => onSearch({ searchWord: value });
  const handleFocus = ({ target: { value } }) => {
    resetSearch();
    if (value) {
      handleSearch(value);
    }
    setFocused(true);
  };
  const handleBlur = () => {
    setFocused(false);
  };
  useOnClickOutside(ref, () => {
    setFocused(false);
  });

  return (
    <div
      className="myHeaderSearch"
      style={{
        display: 'inline-block', position: 'relative', zIndex: '99',
      }}
      ref={ref}
    >
      <Input.Group>
        <Search
          value={searchValue}
          onChange={({ target: { value: eventValue } }) => {
            setValue(eventValue);
          }}
          enterButton={enterButton}
          className={className}
          placeholder={placeholder}
          onSearch={handleSearch}
          onFocus={handleFocus}
          style={style}
        />
      </Input.Group>
      <div
        style={{
          maxHeight: '300px',
          overflow: 'auto',
          background: '#fff',
          padding: '5px 10px',
          width: '100%',
        }}
      >
        {loading && <Spin />}
        {focused
          && (data || []).map((item) => (
            <div key={`search${item.id}`}>
              <Link href="/product/[id]" as={`/product/${item.id}`}>
                <a onClick={handleBlur}>{item.langs[0].name}</a>
              </Link>
            </div>
          ))}
      </div>
    </div>
  );
}

SearchComponent.propTypes = {
  placeholder: PropTypes.string,
  className: PropTypes.string,
  onSearch: PropTypes.func,
  style: PropTypes.objectOf(Object),
  enterButton: PropTypes.bool,
};
SearchComponent.defaultProps = {
  enterButton: undefined,
  style: {},
};

export default SearchComponent;
