import { useMemo } from 'react';
export default ((cartData, promoCodeData) => {
  const fullPrice = useMemo(() => cartData.reduce((a, b) => (a.price * a.count || a) + b.price * b.count, 0), [cartData]);
  const fullNewPrice = useMemo(() => {
    if (!promoCodeData) return null;
    const reduce = cartData.reduce((a, b) => (a.price * a.count || a) + b.price * b.count, 0);
    return promoCodeData ? reduce * (100 - promoCodeData.percent) / 100 : reduce;
  }, [promoCodeData, cartData]);
  const deliveryPrice = useMemo(() => (fullNewPrice || fullPrice) > 20000 ? 0 : 800, [fullNewPrice]);
  const total = useMemo(() => (fullNewPrice || fullPrice) + deliveryPrice, [fullNewPrice, deliveryPrice, fullPrice, cartData]);
  return {
    fullPrice,
    fullNewPrice,
    deliveryPrice,
    total
  };
});
