import { useMemo } from 'react';
export default (function (cartData, promoCodeData) {
  var fullPrice = useMemo(function () {
    return cartData.reduce(function (a, b) {
      return (a.price * a.count || a) + b.price * b.count;
    }, 0);
  }, [cartData]);
  var fullNewPrice = useMemo(function () {
    if (!promoCodeData) return null;
    var reduce = cartData.reduce(function (a, b) {
      return (a.price * a.count || a) + b.price * b.count;
    }, 0);
    return promoCodeData ? reduce * (100 - promoCodeData.percent) / 100 : reduce;
  }, [promoCodeData, cartData]);
  var deliveryPrice = useMemo(function () {
    return (fullNewPrice || fullPrice) > 20000 ? 0 : 800;
  }, [fullNewPrice]);
  var total = useMemo(function () {
    return (fullNewPrice || fullPrice) + deliveryPrice;
  }, [fullNewPrice, deliveryPrice, fullPrice, cartData]);
  return {
    fullPrice: fullPrice,
    fullNewPrice: fullNewPrice,
    deliveryPrice: deliveryPrice,
    total: total
  };
});
