import Axios from '../services/Axios';
import { getDataListByPagination, getDataListByPaginationError, getDataListByPaginationSuccess } from './action';
import { selectLang } from '../user/reducer';
const dataByName = {
  buyProductList: {
    url: '/api/user/orders/all',
    method: 'post'
  }
};

async function getDataListByPaginationRequest({
  name,
  page,
  lang
}) {
  const {
    data: {
      pages,
      result: {
        rows
      }
    }
