    data: {
      page,
      offset: 12 * (page - 1),
      limit: 12,
      lang
    }
  }));
  return {
    pages,
    data: rows
  };
}

function* workGetDataListByPagination({
  payload: {
    name,
    page
  }
}) {
  try {
    const lang = yield select(selectLang);
    const res = yield call(getDataListByPaginationRequest, {
      name,
      page: page || 1,
      lang
    });
    yield put(getDataListByPaginationSuccess(_objectSpread({}, res, {
      name,
      page
    })));
  } catch (error) {
    yield put(getDataListByPaginationError({
      name
    }));
  }
}

function* watchGetDataListByPagination() {
  yield takeEvery(getDataListByPagination, workGetDataListByPagination);
}

export default function* () {
  yield all([watchGetDataListByPagination()]);
