import { all, takeEvery, call, put } from 'redux-saga/effects';
import { searchProduct, searchProductError, searchProductSuccess } from './action';
import Axios from '../services/Axios';

function getSearchedProductsRequest(searchWord) {
  return Axios.request({
    method: 'post',
    url: '/api/product/homePage/search',
    data: {
      searchWord
    }
  });
}

function* workSearchProduct({
  payload: {
    searchWord
  }
}) {
  try {
    const {
      data: {
        result
      }
    } = yield call(getSearchedProductsRequest, searchWord);
    yield put(searchProductSuccess({
      data: result
    }));
  } catch (error) {
    yield put(searchProductError());
  }
}

function* watchSearchProduct() {
  yield takeEvery(searchProduct, workSearchProduct);
}

export default function* () {
  yield all([watchSearchProduct()]);
}
